import { ProductsService } from '../../services/products.service';
import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.scss']
})
export class FooterComponent {
  searchText: string = "";
  products!: IProduct[];

  constructor(private productS: ProductsService) {
    this.productS.getAll().subscribe({
      next: (data) => {this.products = data},
      error: (error) => {console.log(error.first = (second) => {third})}
    });
  }

  hihi(id: any){
    console.log(id)
  }
}
<app-header [products]="products" (onRemove)="hihi($event)" />

<div>
  Lọc theo tên:
  <input type="text" [(ngModel)]="searchText" class="border-2" />
</div>

<table class="table table-bordered">
  <thead>
    <tr>
      <th>#</th>
      <th>Name</th>
      <th>Mã</th>
      <th>Image</th>
      <th>Action</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let item of products | searchBox : searchText, index as i">
      <td>{{i + 1}}</td>
      <td>{{item.name}}</td>
      <td>{{item.price | chancePrice }}</td>
      <td>
        <img [src]="item.imageUrl" width="100" alt="">
      </td>
      <td>
        <button class="btn btn-danger">Sửa</button>
        <button class="btn btn-primary ms-2">Xóa</button>
      </td>
    </tr>
  </tbody>
</table>
