import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {
  title: string = 'hello world';
  // changeInput(event: Event) {
  //   this.title = (event.target as HTMLInputElement).value
  // }

  @Input() products!: IProduct[];
  @Output() onRemove = new EventEmitter()
  onClick(id:any) {
    this.onRemove.emit(id);
  }
}
<!-- <input (keyup)="changeInput($event)" type="text"> -->
<input [(ngModel)]="title" type="text">
<p>{{title}}</p>

<!-- <p>{{products}}</p> -->
<tbody>
  <tr *ngFor="let item of products, index as i">
    <!-- <td>{{i + 1}}</td> -->
    <td>{{item.name}}</td>
    <td>{{item.price | currency : "VND" : "symbol" : "1.3-3" }}</td>
    <td>
      <img [src]="item.imageUrl" width="100" alt="">
    </td>
    <td>
      <button class="btn btn-danger" (click)="onClick(item.id)">Sửa</button>
      <button class="btn btn-primary ms-2">Xóa</button>
    </td>
  </tr>
</tbody>
